package cn.ggdo.system.jcgm.controller;public class DBEntity {	public String dbDriver;	public String dbUrl;	public String dbUser;	public String dbPass;	public String dbTable;	public String dbTableAlias;	public String dbTableSub;	public String getDbDriver() {		return dbDriver;	}	public void setDbDriver(String dbDriver) {		this.dbDriver = dbDriver;	}	public String getDbUrl() {		return dbUrl;	}	public void setDbUrl(String dbUrl) {		this.dbUrl = dbUrl;	}	public String getDbUser() {		return dbUser;	}	public void setDbUser(String dbUser) {		this.dbUser = dbUser;	}	public String getDbPass() {		return dbPass;	}	public void DBTableSub(String dbPass) {		this.dbPass = dbPass;	}	public String getDbTable() {		return dbTable;	}	public void setDbTable(String dbTable) {		this.dbTable = dbTable;	}	public String getDbTableAlias() {		return dbTableAlias;	}	public void setDbTableAlias(String dbTableAlias) {		this.dbTableAlias = dbTableAlias;	}	public String getDbTableSub() {		return dbTableSub;	}	public void setDbTableSub(String dbTableSub) {		this.dbTableSub = dbTableSub;	}	public void setDbPass(String dbPass) {		this.dbPass = dbPass;	}}