package cn.ggdo.system.jcgm.dbToMybatisXml;public class GenerateMybatisXml {	/**	 * 生成方法	 */	public void generate() {		System.out.println("生成[mybatisXml]......");		try {			Handle handle = new Handle();			handle.setNameUntil();			handle.writeGenerate();			System.out.println("生成[mybatisXml]成功!");		} catch (Exception e) {			System.out.println("生成[mybatisXml]失败!");		}	}}